package Java_Programs;

public class ElseIFLadder_Prog1 {

	short num=200;
	ElseIFLadder_Prog1()
	{

		if(num>300)
		{
			System.out.println("number is greater than 300");
		}
		else if(num<300)
		{
			System.out.println("num is less than 300");
		}
		
		else if(num==300)
		{
			System.out.println("num is equal to 300");
		}
	}
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		ElseIFLadder_Prog1 ep= new ElseIFLadder_Prog1();
		
		
	}

}
